contract AssetToken {

    uint256 public totalSupply;
    string public name;
    string public symbol;

    constructor(uint256 initialSupply, string memory tokenName, string memory tokenSymbol) public {
        totalSupply = initialSupply * 10 ** 18;
        name = tokenName;
